<?php	include "init.php";		$act = get("act");		if($act == "faq"){			$smarty->assign("title","FAQ - ".$setting["sitename"]);		template("faq.tpl");			}	elseif($act == "tos"){				$smarty->assign("title","TOS - ".$setting["sitename"]);		template("tos.tpl");			}	elseif($act == "payments"){			$smarty->assign("title","Payments - ".$setting["sitename"]);				$page = get("page");		if(empty($page)) 	$page=1;		$sql=$db->link->query("SELECT DISTINCT count(id) FROM news");		$coun=$sql->fetch_array();		$count=$coun[0];		$start=($page-1)*10;		$end=10;					$payment = $db->select("invoices","invoice,payee_name,amount,pdate","","id DESC","$start,$end");		$payments = array();		while($result = $payment->fetch_assoc()){			$payments[] = $result;		}		$smarty->assign("payments",$payments);		$init->paging($start,$count,10,$setting["siteurl"]."/home/payments?");				template("payments.tpl");	}	elseif($act == "error"){				if($_GET["type"]=="404"){			$smarty->assign("title","Error 404 - ".$setting["sitename"]);			template("404_error.tpl");		}		elseif($_GET["type"]=="403"){			$smarty->assign("title","Error 403 - ".$setting["sitename"]);			template("403_error.tpl");		}		else {			$smarty->assign("title","Error 500 - ".$setting["sitename"]);			template("404_error.tpl");		}	}			else {				redir("/");	}?>						